import{_ as a,a as i,b as t,c as r,d as e,e as o,f as s}from"./chunks/image75.du3hJVnt.js";import{_ as p,c as n,o as h,ae as u}from"./chunks/framework.COrXKI8X.js";const A=JSON.parse('{"title":"状态显示与控制","description":"","frontmatter":{},"headers":[],"relativePath":"app/status.md","filePath":"app/status.md"}'),c={name:"app/status.md"};function d(_,l,m,P,f,b){return h(),n("div",null,l[0]||(l[0]=[u('<h1 id="状态显示与控制" tabindex="-1">状态显示与控制 <a class="header-anchor" href="#状态显示与控制" aria-label="Permalink to &quot;状态显示与控制&quot;">​</a></h1><h2 id="控制机器人移动" tabindex="-1">控制机器人移动 <a class="header-anchor" href="#控制机器人移动" aria-label="Permalink to &quot;控制机器人移动&quot;">​</a></h2><ol><li><p>在APP首页，点击界面上方的遥控图标按钮，右下角会弹出一个控制机器人移动的遥控窗口 <img src="'+a+'" alt="APP首页"></p></li><li><p>遥控操作：</p><ul><li>通过点击方向按钮来控制机器人前后左右移动</li><li>中间按钮为急停按钮，可以控制机器人停止移动</li><li>每多点击一次方向按钮，都会使得机器人移动速度等级上升或者下降一个等级</li></ul></li><li><p>遥控速度调整：</p><ul><li>在APP首页，点击点管理图标，进入到【功能设置】界面，点击右上角展开图标 <img src="'+i+'" alt="功能设置"></li><li>遥控速度设置：高速为1.2 m/s，低速为0.6 m/s（默认为低速）</li></ul></li><li><p>遥控类型：</p><ul><li>在隐藏设置区，可将遥控器切换为摇杆类型（默认为箭头遥控器） <img src="'+t+'" alt="摇杆遥控"></li></ul></li></ol><h2 id="机器人信息显示" tabindex="-1">机器人信息显示 <a class="header-anchor" href="#机器人信息显示" aria-label="Permalink to &quot;机器人信息显示&quot;">​</a></h2><p>在APP首页，点击界面上方的信息显示图标，能获取机器人的移动速度，显示转向角，电量等信息 <img src="'+r+'" alt="机器人信息"></p><p>信息图标说明：</p><ul><li>机器人名称</li><li>当前机器人速度</li><li>当前机器人旋转角度</li><li>实时显示网络信号</li><li>显示/隐藏云台控制遥控器</li><li>查看机器人的底盘状态</li><li>录制包的图标</li></ul><h2 id="底盘状态" tabindex="-1">底盘状态 <a class="header-anchor" href="#底盘状态" aria-label="Permalink to &quot;底盘状态&quot;">​</a></h2><p>在APP首页，点击界面上方的信息显示图标，在弹框中点击【底盘状态】按钮，可以显示机器人的GPS、每个红外线状态和距离、超声波状态和距离、电压信息 <img src="'+e+'" alt="底盘状态"></p><h2 id="录包" tabindex="-1">录包 <a class="header-anchor" href="#录包" aria-label="Permalink to &quot;录包&quot;">​</a></h2><p>在APP首页，点击界面上方的信息显示图标，在弹框中点击录包图标，弹出录包界面 <img src="'+o+'" alt="录包界面"></p><p>录包功能说明：</p><ul><li>录包的功能在于发现问题后，可以将录制的包发送给工作人员，方便快速发现问题</li><li>录制包时，需要在点击【开始建图】之前，先对录包进行操作</li><li>当录制图标为红色时，表示正在录包</li></ul><p>录包类型：</p><ul><li><strong>导航模式</strong>：地图和导航的包全部都进行录制</li><li><strong>建图/定位</strong>：当机器人发生定位不准确时，可进行录制</li></ul><h2 id="云台控制" tabindex="-1">云台控制 <a class="header-anchor" href="#云台控制" aria-label="Permalink to &quot;云台控制&quot;">​</a></h2><p>在APP首页，点击界面上方的信息显示图标，在弹框中点击【云台控制】按钮，右下角会弹出一个云台控制遥控器 <img src="'+s+'" alt="云台控制"></p><p>云台控制操作：</p><ul><li>通过点击方向按钮来控制云台上下左右转动</li><li>中间按钮为停止转动</li><li>右下角为云台复位按钮</li></ul><h2 id="外接设备控制" tabindex="-1">外接设备控制 <a class="header-anchor" href="#外接设备控制" aria-label="Permalink to &quot;外接设备控制&quot;">​</a></h2><p>喇叭、灯光、警灯控制：</p><ol><li>在APP首页，点击点管理图标，进入到【功能设置】界面</li><li>点击右上角展开图标，展开隐藏设置区</li><li>点击相应图标按钮，控制喇叭、灯光、警灯开关</li></ol><h2 id="音频收藏" tabindex="-1">音频收藏 <a class="header-anchor" href="#音频收藏" aria-label="Permalink to &quot;音频收藏&quot;">​</a></h2><ol><li>在APP首页，点击点管理图标，选择【音频收藏】，可以查看已上传的音频列表</li><li>点击【播放】可以播放该音频，同时能播放多个音频</li><li>点击右上角【停止】，可以停止所有播放声音</li><li>上传音频：点击顶部右上角【上传】，选择移动设备文件夹中的.wav格式音频</li></ol>',24)]))}const x=p(c,[["render",d]]);export{A as __pageData,x as default};
